function getNetworkConnectionType(state) {
    switch (state) {
        case 'none':
            return 'unavailable';
        case 'cellular':
            return 'cell';
        case 'ethernet':
            return 'wired';
        case 'wimax':
        case 'wifi':
            return 'wifi';
        case 'bluetooth':
        case 'other':
        case 'unknown':
        case 'vpn':
            return 'unknown';
        default:
            return 'unknown';
    }
}

export { getNetworkConnectionType as default };
