import { TurboModuleRegistry, NativeModules } from 'react-native';

const isTurboModuleEnabled = () => global.__turboModuleProxy != null;
const NativeBugsnagPerformance = isTurboModuleEnabled()
    ? TurboModuleRegistry.get('BugsnagReactNativePerformance')
    : NativeModules.BugsnagReactNativePerformance;

export { NativeBugsnagPerformance, NativeBugsnagPerformance as default };
